mapper.select:
SELECT movieListId, movieCd, movieNm, movieNmEn, prdtYear, openDt, typeNm, prdtStatNm, nationAlt, genreAlt, repNationNm, repGenreNm, directors, companys
FROM MovieList
WHERE movieNm = #{movieNm}

SELECT movieListId, movieCd, movieNm, movieNmEn, prdtYear, openDt, typeNm, prdtStatNm, nationAlt, genreAlt, repNationNm, repGenreNm, directors, companys
FROM MovieList
WHERE 1=1

    AND movieNm LIKE #{movieNm}


    AND directorsJson LIKE '%'+#{directorName}+'%'


    AND prdtYear = #{prdtYear}


    AND openDt >= #{openDtStart}


    AND openDt  <=  #{openDtEnd}

ORDER BY openDt DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM MovieList
WHERE 1=1

    AND movieNm LIKE CONCAT('%', #{movieNm}, '%')


    AND directorsJson LIKE CONCAT('%', #{directorName}, '%')


    AND prdtYear = #{prdtYear}


    AND openDt >= #{openDtStart}


    AND openDt  <=  #{openDtEnd}
